SELECT EMPRESA_ID,
	   DETALHE_ID,
	   RECIBO,
	   DESCRICAO,
	   REGISTRO,
	   VIGENCIA,
	   XML
FROM wdp.ESOCIAL_FILA
WHERE REGISTRO in ('S-1000', 'S-1005', 'S-1020', 'S-1030', 'S-1040', 'S-1050', 'S-2200', 'S-2300', 'S-2205', 'S-2206', 'S-2230')
	AND  RECIBO <> ''
	AND  ambiente = 1
ORDER BY 1, 5, 2