SELECT DISTINCT TABEMPRESAS.CODIGO AS EMPRESA,
       TABVERBAS.CODIGO,
       TABVERBAS.NOME,
       TABVERBAS.TIPOUNIDADE,
       TABVERBAS.MEDIA13,
       TABVERBAS.MEDIAFERIAS
  FROM TABVERBAS,
       TABEMPRESAS
 WHERE EXISTS (SELECT 1
                 FROM TABFOLHA F INNER JOIN TABFOLHAFICHAFINANCEIRA FD  
                   ON FD.IDMASTER = F.IDMASTER
                  AND TABVERBAS.CODIGO = FD.CODIGOVERBA
                  AND TABEMPRESAS.CODIGO = F.CODIGOEMPRESA)
 ORDER BY 1,2