SELECT P.CODIGOEMPRESA,
       P.CODIGOFILIAL,
       P.CODIGO CODFUNC,       
       F.EFETIVADA,
       F.TIPOPROCESSAMENTO,
       E.CODIGO CODVERBA,
       F.DATAFOLHA,
       FD.REFERENCIA,
       FD.VALOR,
       E.TIPOPD,
	   E.TIPOUNIDADE
  FROM TABFOLHA F INNER JOIN TABFOLHAFICHAFINANCEIRA FD 
       ON FD.IDMASTER = F.IDMASTER
    INNER JOIN TABPESSOAS P 
       ON FD.CODIGOPESSOA = P.CODIGO
    INNER JOIN TABVERBAS E 
       ON E.CODIGO = FD.CODIGOVERBA
    WHERE P.CODIGOEMPRESA = F.CODIGOEMPRESA
	  AND EXTRACT(YEAR FROM F.DATAFOLHA) >= EXTRACT(YEAR FROM CURRENT_DATE) -3
  ORDER BY 1,2,3,7,6