SELECT TABPESSOAS.CODIGOEMPRESA,
       TABPESSOAS.CODIGOFILIAL,
       TABFOLHAHISTORICOPESSOA.*
FROM TABPESSOAS
INNER JOIN TABFOLHAHISTORICOPESSOA
ON (TABPESSOAS.CODIGO = TABFOLHAHISTORICOPESSOA.CODIGOPESSOA)
WHERE TIPODEMOVIMENTO = 17
ORDER BY TABPESSOAS.CODIGOEMPRESA, TABPESSOAS.CODIGO, TABFOLHAHISTORICOPESSOA.CODIGOPESSOA, DATA
