SELECT h.CD_EMPRESA,
       h.CD_EMPREGADO,
       h.CD_TABAFASTAMENTORAIS,
       h.DT_INICIOAFAST,
       DATEADD(DAY, -1, h.DT_FIMAFAST) AS DT_FIMAFAST,
       DATEDIFF(DAY, h.DT_INICIOAFAST, h.DT_FIMAFAST) AS DIAS
  FROM HISTORICOAFASTEMPREG h
 ORDER BY h.CD_EMPRESA,
          h.CD_EMPREGADO,
          h.DT_INICIOAFAST;