SELECT CD_ADMCONT,
       NR_REGISTRO,
	   NR_CNPJCPF,
	   DS_EMAIL,
	   CD_MUNICIPIO,
	   NM_ADMCONT,
	   DS_ENDERECO,
	   DS_BAIRRO,
	   NR_TELEFONE,
	   TP_PROFISSIONAL,
	   DS_ORGAOEXPRG,
	   NR_RG,
	   NR_CEP,
	   NR_CELULAR,
	   DS_COMPLEMENTO,
	   NR_ENDERECO,
	   (SELECT m.DS_MUNICIPIO
	      FROM MUNICIPIO m
	     WHERE m.CD_MUNICIPIO = ADMCONT.CD_MUNICIPIO) AS MUNICIPIO,
	   (SELECT m.CD_MUNICIPIOIBGE
	      FROM MUNICIPIO m
	     WHERE m.CD_MUNICIPIO = ADMCONT.CD_MUNICIPIO) AS MUNICIPIO_IBGE,
	   (SELECT e.SG_ESTADO
	      FROM ESTADO e
	     WHERE e.CD_ESTADO = (SELECT m.CD_ESTADO
	                            FROM MUNICIPIO m
	                           WHERE m.CD_MUNICIPIO = ADMCONT.CD_MUNICIPIO)) AS CD_ESTADO,
	   DT_NASCIMENTO
  FROM ADMCONT
 WHERE ADMCONT.CD_ADMCONT IN (SELECT DISTINCT CD_CONTADOR FROM CADFILFOLHAATRIB)
 ORDER BY CD_ADMCONT;