SELECT CD_EMPRESA,
       CD_EMPREGADO,
       CD_DEPENDENTE,
       NM_DEPENDENTE,
       DT_NASCIMENTO,
       DT_ENTREGACERTIDAO,
       (SELECT DS_MUNICIPIO
          FROM MUNICIPIO
         WHERE MUNICIPIO.CD_MUNICIPIO = DEPENDENTESALFAMILIA.CD_MUNICIPIO) AS CIDADE,
       DS_CARTORIOREGISTRO,
       NR_LIVRO,
       NR_FOLHA,
       NR_CPF,
       NR_REGISTRO,
       DT_BAIXA,
       TP_PARENTESCO,
       (SELECT SG_ESTADO
          FROM ESTADO
         WHERE ESTADO.CD_ESTADO = (SELECT CD_ESTADO
                                     FROM MUNICIPIO
                                    WHERE MUNICIPIO.CD_MUNICIPIO = DEPENDENTESALFAMILIA.CD_MUNICIPIO)) AS ESTADO
  FROM DEPENDENTESALFAMILIA
 ORDER BY 1, 2, 3;