SELECT e.CD_EMPRESA,
       e.CD_FILIAL,
       e.TP_REGISTRO,
       empregado.CD_EMPREGADO,
       e.DT_RECIBO,
       (SELECT DT_INICIOMES FROM CNET_DATAINICIOMES(e.NR_ANOMESINICIO)) AS COMPETENCIA,
       CAST(e.DS_CAMPOS AS VARCHAR(300)) AS DS_CAMPOS,
       e.NR_RECIBO,
       e.DT_MOVIMENTO,
       CAST(e.DS_XML AS VARCHAR(9000)) AS DS_XML
  FROM ESOCIALLOG e
  LEFT JOIN EMPREGADO ON EMPREGADO.CD_EMPRESA = e.CD_EMPRESA 
  AND EMPREGADO.NR_ESOCIALHASH = e.NR_HASH
  WHERE e.NR_RECIBO IS NOT NULL
    AND e.TP_REGISTRO IN ('S1000', 'S1005', 'S1020', 'S1030', 'S1050', 'S2200', 'S2205', 'S2206', 'S2230', 'S2300')
    AND e.NR_RECIBO LIKE '1.1.%'
  ORDER BY 1, 2, 3, 4, 5;