SELECT a.CD_EMPRESA,
       a.CD_EMPREGADO,
       a.DT_ACUMULO,
       a.CD_ITEMPROVDESC,
       a.SQ_ITEMPROVDESC,
       a.VL_BASE,
       a.VL_UNITARIO,
       a.VL_TOTAL
  FROM ACUMVERBA a
 WHERE a.DT_ACUMULO > DATEADD(-4 YEAR TO CURRENT_DATE)
 AND a.SQ_ITEMPROVDESC NOT IN (80,81)
 ORDER BY a.CD_EMPRESA,
          a.CD_EMPREGADO,
          a.DT_ACUMULO,
          a.CD_ITEMPROVDESC,
       	  a.SQ_ITEMPROVDESC;