SELECT CD_EMPRESA,
       CD_EMPREGADO,
       DT_INICIOAQUISITIVO,
       DT_FIMAQUISITIVO,
       SUM(NR_DIASLIQUIDO) AS DIAS_DIREITO,
       SUM(NR_DIASFALTA) AS DIAS_FALTAS,
       SUM(NR_DIASABONO) AS DIAS_ABONO,
       SUM(NR_DIASGOZAR) AS DIAS_GOZADO
  FROM HISTORICOFERIAS
 GROUP BY CD_EMPRESA, CD_EMPREGADO, DT_INICIOAQUISITIVO, DT_FIMAQUISITIVO
 ORDER BY CD_EMPRESA,
          CD_EMPREGADO,
          DT_INICIOAQUISITIVO;