SELECT EMPREGADOPLSAUDEDEP.CD_EMPRESA,
       EMPREGADOPLSAUDEDEP.CD_EMPREGADO,
       EMPREGADOPLSAUDEDEP.CD_DEPENDENTE,
       FLPVIGENCIAPLSAUDE.CD_FLPOPERADORAPLSAUDE,
       EMPREGADOPLSAUDEDEP.DT_INICIOVIGENCIA,
       EMPREGADOPLSAUDEDEP.VL_PLANOMENSAL
  FROM EMPREGADOPLSAUDEDEP
  LEFT JOIN FLPVIGENCIAPLSAUDE
    ON FLPVIGENCIAPLSAUDE.CD_FLPVIGENCIAPLSAUDE = EMPREGADOPLSAUDEDEP.CD_FLPVIGENCIAPLSAUDE
 ORDER BY 1, 2, 3, 4;