SELECT EMPREGADOPLSAUDE.CD_EMPRESA,
       EMPREGADOPLSAUDE.CD_EMPREGADO,
       FLPVIGENCIAPLSAUDE.CD_FLPOPERADORAPLSAUDE,
       FLPVIGENCIAPLSAUDE.DT_INICIOVIGENCIA,
       EMPREGADOPLSAUDE.VL_PLANOMENSAL
  FROM EMPREGADOPLSAUDE
  LEFT JOIN FLPVIGENCIAPLSAUDE
    ON FLPVIGENCIAPLSAUDE.CD_FLPVIGENCIAPLSAUDE = EMPREGADOPLSAUDE.CD_FLPVIGENCIAPLSAUDE
 ORDER BY 1, 2, 3;