SELECT CD_EMPRESA,
       (SELECT adm.CD_SOCIOGER
          FROM ADMCONT adm
         WHERE adm.CD_ADMCONT = EMPQUADROSOCIO.CD_ADMCONT) AS CD_SOCIOGER,
       DT_ENTRADA,
       DT_SAIDA,
       NR_JUNTACOMERCIAL,
       PC_CAPITALTOTAL,
       PC_CAPITALVOTANTE,
       NR_COTACAPITAL,
       VL_COTACAPITAL,
       CD_TABQUALIFICACAOSOCIO,
       (SELECT tab.CD_LEGAL
          FROM TABQUALIFICACAOREPLEGAL tab
         WHERE tab.CD_TABQUALIFICACAOREPLEGAL = EMPQUADROSOCIO.CD_TABQUALIFICACAOREPLEGAL) AS TIPO_RESPONSAVEL,
       NR_CPFREPRESENTANTELEGAL
  FROM EMPQUADROSOCIO
 WHERE (SELECT adm.CD_SOCIOGER
          FROM ADMCONT adm
         WHERE adm.CD_ADMCONT = EMPQUADROSOCIO.CD_ADMCONT) IS NOT NULL
 ORDER BY CD_EMPRESA,
          CD_EMPQUADROSOCIO;