SELECT DISTINCT emp.CD_EMPRESA,
       p.CD_ITEMPROVDESC,
       p.DS_ITEMPROVDESC,
       form.TP_UNIDADE AS UNIDADE,
       form.PC_INDICE AS PERCENTUAL,
       form.ST_REPOUSO AS DESCANSO_REMUNERADO,
       inc.TP_INCIDEBASEFERIAS,
       inc.TP_INCIDEBASEDECIMOTERC
FROM CADFILFOLHAATRIB emp,
     PROVDESCRH p
LEFT JOIN TABELAINCIDENCIA AS inc
	ON inc.CD_INCIDE = p.CD_INCIDE
	AND inc.CD_INCIDE <> 1
LEFT JOIN TABELAFORMULA AS form
	ON form.CD_FORMULA = p.CD_FORMULA
ORDER BY emp.CD_EMPRESA,
         p.CD_ITEMPROVDESC