SELECT CD_SINDI,
       DS_SINDI,
       DS_ENDERECO,
       DS_BAIRRO,
       (SELECT DS_MUNICIPIO
          FROM MUNICIPIO
         WHERE MUNICIPIO.CD_MUNICIPIO = SINDICATORH.CD_MUNICIPIO) AS CIDADE,
       (SELECT SG_ESTADO
          FROM ESTADO
         WHERE ESTADO.CD_ESTADO = (SELECT CD_ESTADO
                                     FROM MUNICIPIO
                                    WHERE MUNICIPIO.CD_MUNICIPIO = SINDICATORH.CD_MUNICIPIO)) AS ESTADO,
       NR_CEP,
       NR_TELEFONE,
       NR_CNPJ,
       NR_ENDERECO,
       DS_COMPLEMENTO,
       NR_FAX,
       VL_PISOCATEG,
       NR_MESMEDIAFERIAS,
       NR_MESDICIDIO
  FROM SINDICATORH
 WHERE CD_CATSINDI = 1
 ORDER BY 1;