SELECT FP_FUNCIONARIO_SADE.ID_EMPRESA,
       FP_FUNCIONARIO_SADE.CODIGO,
       FP_FUNCIONARIO_SADE.TIPO,
       COD_AFAST_CONF_VIEW.CODIGO_AFASTAMENTO,
       FP_FUNCIONARIO_SADE_AFAST.ID_FUNC_SADE,
       FP_FUNCIONARIO_SADE_AFAST.ID_COD_AFASTAMENTO,
       FP_FUNCIONARIO_SADE_AFAST.DATA_AFASTAMENTO,
       FP_FUNCIONARIO_SADE_AFAST.DATA_RETORNO
FROM FP_FUNCIONARIO_SADE_AFAST
INNER JOIN FP_FUNCIONARIO_SADE
	ON FP_FUNCIONARIO_SADE.ID = FP_FUNCIONARIO_SADE_AFAST.ID_FUNC_SADE
LEFT JOIN COD_AFAST_CONF_VIEW
	ON COD_AFAST_CONF_VIEW.ID_HELP_AFAST = FP_FUNCIONARIO_SADE_AFAST.ID_COD_AFASTAMENTO
ORDER BY 1, 3, 2, 5