SELECT DISTINCT EST_RAZAO_SOCIAL_AGENTE,
       EST_CNPJ_AGENTE,
       EST_ENDERECO_AGENTE,
       EST_NUMERO_AGENTE,
       EST_BAIRRO_AGENTE,
       EST_CEP_AGENTE,
       GE_CIDADES.CODIGO AS CODIGO_IBGE
FROM FP_FUNCIONARIO_SADE
LEFT JOIN GE_CIDADES
	ON GE_CIDADES.ID = FP_FUNCIONARIO_SADE.EST_ID_MUNICIPIO_AGENTE
WHERE EST_RAZAO_SOCIAL_ENSINO IS NOT NULL
ORDER BY 1