SELECT FP_FUNCIONARIO_SADE.ID_EMPRESA,
       FP_FUNCIONARIO_SADE.CODIGO,
       FP_FUNCIONARIO_SADE.TIPO,
       FP_FUNCIONARIO_SADE_EXAMES.DATA_EXAME,
       FP_FUNCIONARIO_SADE_EXAMES.TIPO_EXAME
FROM FP_FUNCIONARIO_SADE JOIN FP_FUNCIONARIO_SADE_EXAMES
	ON FP_FUNCIONARIO_SADE.ID = FP_FUNCIONARIO_SADE_EXAMES.ID_FUNC_SADE
ORDER BY 1, 3, 2