SELECT DISTINCT FP_FUNCIONARIO_SADE.ID_EMPRESA,
	   FP_CARGOS.CODIGO,
       FP_CARGOS.NOME,
       (SELECT FP_CBO.CODIGO FROM FP_CBO WHERE FP_CBO.ID = FP_CARGOS.CBO) AS CBO
FROM FP_CARGOS
LEFT JOIN FP_FUNCIONARIO_SADE_SALARIO
	ON FP_FUNCIONARIO_SADE_SALARIO.ID_CARGO = FP_CARGOS.ID
LEFT JOIN FP_FUNCIONARIO_SADE
	ON FP_FUNCIONARIO_SADE.ID = FP_FUNCIONARIO_SADE_SALARIO.ID_FUNC_SADE
ORDER BY 1, 2