SELECT FP_FUNCIONARIO_SADE.ID_EMPRESA,
	   FP_FUNCIONARIO_SADE.CODIGO,
	   FP_FUNCIONARIO_SADE.TIPO,
       GE_PESSOAS.REG_CONS_REG_NOME,
       GE_PESSOAS.REG_CONS_REG_NUMERO,
       GE_PESSOAS.REG_CONS_REG_UF,
       GE_PESSOAS.REG_CONS_REG_DATA_EXP,
       GE_PESSOAS.REG_CONS_REG_DATA_VENC
FROM FP_FUNCIONARIO_SADE
INNER JOIN GE_PESSOAS
	ON FP_FUNCIONARIO_SADE.ID_PESSOAS = GE_PESSOAS.ID
WHERE GE_PESSOAS.REG_CONS_REG_NUMERO IS NOT NULL
ORDER BY 1, 3, 2
