SELECT FP_FUNCIONARIO_SADE.ID_EMPRESA,
       FP_FUNCIONARIO_SADE.CODIGO,
       FP_FUNCIONARIO_SADE.TIPO,
       FP_DEPENDENTES.CODIGO AS CODIGO_FILHO,
       GE_PESSOAS.NOME,
       GE_PESSOAS.DATA_NASCIMENTO,
       GE_PESSOAS.DOCUMENTO,
       FP_DEPENDENTES.ID_HELP_GRAU_PARENTESCO,
       FP_DEPENDENTES.CARTORIO,
       FP_DEPENDENTES.LIVRO,
       FP_DEPENDENTES.FOLHA,
       FP_DEPENDENTES.REGISTRO,
       FP_DEPENDENTES.DATA_ENTREGA_REG,
       FP_DEPENDENTES.DATA_FIM_DEPENDENCIA,
       (SELECT GE_CIDADES.NOME FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_PESSOAS.ID_NATURALIDADE) AS LOCAL_NASCIMENTO
FROM FP_DEPENDENTES
INNER JOIN FP_FUNCIONARIO_SADE
	ON FP_FUNCIONARIO_SADE.ID_PESSOAS = FP_DEPENDENTES.ID_PESSOA_RESP
LEFT JOIN GE_PESSOAS
	ON FP_DEPENDENTES.ID_PESSOA_DEP = GE_PESSOAS.ID
ORDER BY 1, 3, 2