SELECT GE_EMPRESAS.*,
       (SELECT GE_LOGRADOURO.DESCRICAO FROM GE_LOGRADOURO WHERE GE_LOGRADOURO.ID = GE_EMPRESAS.ID_LOGRADOURO) AS LOGRADOURO,
       (SELECT GE_CIDADES.NOME FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_EMPRESAS.ID_CIDADE) AS CIDADE,
       (SELECT GE_CIDADES.UF FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_EMPRESAS.ID_CIDADE) AS ESTADO,
       (SELECT GE_CIDADES.CODIGO FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_EMPRESAS.ID_CIDADE) AS IBGE,
       (SELECT GE_NATJUR.CODIGO FROM GE_NATJUR WHERE GE_NATJUR.ID = GE_EMPRESAS_FOLHA.RAIS_ID_NAT_JUR) AS NATUREZA_JURIDICA,
       (SELECT FP_CNAE.CODIGO FROM FP_CNAE WHERE FP_CNAE.ID = GE_EMPRESAS.ID_CNAE) AS CNAE,
       (SELECT FP_CNAEF.CODIGO FROM FP_CNAEF WHERE FP_CNAEF.ID = GE_EMPRESAS.ID_CNAEF) AS CNAE2,
       GE_RESPONSAVEIS.NOME AS NOME_RESP,
       GE_RESPONSAVEIS.NUM_DOC AS NUM_DOC_RESP,
       GE_RESPONSAVEIS.RG AS RG_RESP,
       GE_RESPONSAVEIS.RG_ESTEMIS AS RG_ESTEMIS_RESP,
       GE_RESPONSAVEIS.DDD AS DDD_RESP,
       GE_RESPONSAVEIS.TELEFONE AS TELEFONE_RESP,
       GE_RESPONSAVEIS.E_MAIL AS EMAIL_RESP,
       GE_RESPONSAVEIS.QUALIFICACAO AS QUALIFICACAO_RESP,
       GE_RESPONSAVEIS.ENDERECO AS ENDERECO_RESP,
       GE_RESPONSAVEIS.COMPLEMENTO AS COMPLEMENTO_RESP,
       GE_RESPONSAVEIS.NUMERO AS NUMERO_RESP,
       GE_RESPONSAVEIS.BAIRRO AS BAIRRO_RESP,
       (SELECT GE_LOGRADOURO.DESCRICAO FROM GE_LOGRADOURO WHERE GE_LOGRADOURO.ID = GE_RESPONSAVEIS.ID_LOGRADOURO) AS RESP_LOGRADOURO,
       (SELECT GE_CIDADES.NOME FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_RESPONSAVEIS.ID_MUNICIPIO) AS CIDADE_RESP,
       (SELECT GE_CIDADES.UF FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_RESPONSAVEIS.ID_MUNICIPIO) AS ESTADO_RESP,
       (SELECT GE_CIDADES.CODIGO FROM GE_CIDADES WHERE GE_CIDADES.ID = GE_RESPONSAVEIS.ID_MUNICIPIO) AS IBGE_RESP,
       GE_RESPONSAVEIS.CEP AS CEP_RESP,
       FP_FPAS.CODIGO AS CODIGO_FPAS,
       FP_FPAS.PERC_EMPRES,
       FP_FPAS.COD_TERC,
       FP_FPAS.PERC_TERC,
       FP_FPAS.PERC_SAT,
       FP_FPAS.ISENFILAN,
       FP_FPAS.PERC_SADE,
       (SELECT GE_HELP.CODIGO FROM GE_HELP WHERE GE_HELP.ID = GE_EMPRESAS_FOLHA.ID_GPS) AS CODIGO_GPS,
       (SELECT GE_HELP.CODIGO FROM GE_HELP WHERE GE_HELP.ID = GE_EMPRESAS_FOLHA.SEFIP_ID_COD_REC_FGTS) AS CODIGO_SEFIP
FROM GE_EMPRESAS
LEFT OUTER JOIN GE_EMPRESAS_FOLHA
	ON GE_EMPRESAS_FOLHA.ID_EMPRESA = GE_EMPRESAS.ID
LEFT OUTER JOIN FP_FPAS
	ON GE_EMPRESAS_FOLHA.ID_FPAS = FP_FPAS.ID
LEFT OUTER JOIN GE_RESPONSAVEIS
	ON GE_EMPRESAS_FOLHA.ID_RESP_EMPRESAS = GE_RESPONSAVEIS.ID
ORDER BY 1