SELECT FP_CONTROLE_ESOCIAL.IDEMP,
       FP_CONTROLE_ESOCIAL.CODIGO,
       FP_CONTROLE_ESOCIAL.RECIBO_ENTREGA,
       FP_CONTROLE_ESOCIAL.EVENTO,
       FP_CONTROLE_ESOCIAL.TABELA
  FROM FP_CONTROLE_ESOCIAL
 WHERE FP_CONTROLE_ESOCIAL.EVENTO IN ('S1000', 'S1030', 'S2200', 'S2230', 'S2300')
   AND LEFT(FP_CONTROLE_ESOCIAL.RECIBO_ENTREGA, 3) = '1.1'
 ORDER BY 1, 4, 2;