SELECT DISTINCT FP_FUNCIONARIO_SADE.ID_EMPRESA,
       FP_HORARIOS.CODIGO,
       FP_HORARIOS.DESCRICAO,
       FP_HORARIOS.ENTRADA,
       FP_HORARIOS.SAIDA,
       FP_HORARIOS.DURACAO_JORNADA,
       FP_HORARIO_INTERVALO.INICIO AS INI_INTERVALO,
       FP_HORARIO_INTERVALO.FIM AS FIM_INTERVALO,
       FP_HORARIO_INTERVALO.DURACAO AS DURACAO_INTERVALO
FROM FP_HORARIOS
LEFT JOIN FP_HORARIO_INTERVALO
	ON FP_HORARIOS.ID = FP_HORARIO_INTERVALO.ID_HORARIO
LEFT JOIN FP_FUNCIONARIO_SADE
	ON FP_HORARIOS.ID = FP_FUNCIONARIO_SADE.ID_HORARIO_TRABALHO
ORDER BY 1, 2