SELECT DISTINCT EST_RAZAO_SOCIAL_ENSINO,
       EST_CNPJ_INST_ENSINO,
       EST_ENDERECO_ENSINO,
       EST_NUMERO_ENSINO,
       EST_BAIRRO_ENSINO,
       EST_CEP_ENSINO,
       GE_CIDADES.CODIGO AS CODIGO_IBGE
FROM FP_FUNCIONARIO_SADE
LEFT JOIN GE_CIDADES
	ON GE_CIDADES.ID = FP_FUNCIONARIO_SADE.EST_ID_MUNICIPIO_ENSINO
WHERE EST_RAZAO_SOCIAL_ENSINO IS NOT NULL
ORDER BY 1