SELECT FP_SINDICATOS.ID,
       FP_SINDICATOS.NOME,
       FP_SINDICATOS.ENDERECO,
       FP_SINDICATOS.NUMERO,
       FP_SINDICATOS.COMPLEMENTO,
       FP_SINDICATOS.BAIRRO,
       FP_SINDICATOS.CEP,
       FP_SINDICATOS.DDD,
       FP_SINDICATOS.TELEFONE,
       FP_SINDICATOS.NUM_DOC,
       FP_SINDICATOS.COD_ENTIDADE,
       FP_SIND_FUNCIONARIOS.MES_DISSIDIO,
       FP_SIND_FUNCIONARIOS.MES_CONTRIB_SINDICAL,
       (SELECT GE_CIDADES.NOME FROM GE_CIDADES WHERE GE_CIDADES.ID = FP_SINDICATOS.ID_MUNIC) AS CIDADE,
       (SELECT GE_CIDADES.UF FROM GE_CIDADES WHERE GE_CIDADES.ID = FP_SINDICATOS.ID_MUNIC) AS ESTADO
FROM FP_SINDICATOS
LEFT OUTER JOIN FP_SIND_FUNCIONARIOS
  ON FP_SIND_FUNCIONARIOS.ID_SINDICATOS = FP_SINDICATOS.ID
  ORDER BY 1