SELECT FP_CENTRO_CUSTO.*,
       FP_CNAE.CODIGO AS CNAE,
       FP_FPAS.CODIGO AS FPAS,
       FP_FPAS.COD_TERC AS COD_TERC,
       GE_HELP.CODIGO AS GPS
  FROM FP_CENTRO_CUSTO
  LEFT JOIN FP_CNAE
    ON FP_CNAE.ID = FP_CENTRO_CUSTO.ID_CNAE
  LEFT JOIN FP_FPAS
    ON FP_FPAS.ID = FP_CENTRO_CUSTO.ID_FPAS
  LEFT JOIN GE_HELP
    ON GE_HELP.ID = FP_CENTRO_CUSTO.ID_COD_GPS
 WHERE TOMADOR = 'S'
 ORDER BY 2, 1;