SELECT FP_FUNCIONARIO_SADE.ID_EMPRESA,
       FP_FUNCIONARIO_SADE.CODIGO,
       FP_FUNCIONARIO_SADE.TIPO,
       FP_FUNCIONARIO_SADE_SALARIO.DATA,
       FP_FUNCIONARIO_SADE_SALARIO.VALOR,
       FP_CARGOS.CODIGO AS CODIGO_CARGO,
       FP_FUNCIONARIO_SADE_SALARIO.ID_SINDICATOFUNC,
       FP_FUNCIONARIO_SADE_SALARIO.ID_MOTIVO_REAJUSTE,
       FP_MOTIVO_REAJUSTE.DESCRICAO AS MOTIVO_DESCRICAO
FROM FP_FUNCIONARIO_SADE_SALARIO
INNER JOIN FP_FUNCIONARIO_SADE
	ON FP_FUNCIONARIO_SADE.ID = FP_FUNCIONARIO_SADE_SALARIO.ID_FUNC_SADE
INNER JOIN FP_CARGOS
	ON  FP_FUNCIONARIO_SADE_SALARIO.ID_CARGO = FP_CARGOS.ID
INNER JOIN FP_MOTIVO_REAJUSTE
	ON FP_MOTIVO_REAJUSTE.ID = FP_FUNCIONARIO_SADE_SALARIO.ID_MOTIVO_REAJUSTE
ORDER BY 1, 2, 3