SELECT EMPRESA.*,
       RESPONSAVEL.NOME AS RESP_NOME,
       RESPONSAVEL.CPF AS RESP_CPF,
       RESPONSAVEL.ENDERECO AS RESP_ENDERECO,
       RESPONSAVEL.NUMERO AS RESP_NUMERO,
       RESPONSAVEL.COMPLEMENTO AS RESP_COMPLEMENTO,
       RESPONSAVEL.BAIRRO AS RESP_BAIRRO,
       RESPONSAVEL.UF AS RESP_UF,
       RESPONSAVEL.CEP AS RESP_CEP,
       RESPONSAVEL.COD_CIDADE AS RESP_COD_CIDADE,
       RESPONSAVEL.DDD_FONE AS RESP_DDD,
       RESPONSAVEL.FONE1 AS RESP_FONE,
       RESPONSAVEL.EMAIL AS RESP_EMAIL
FROM EMPRESA
LEFT JOIN RESPONSAVEL
	ON RESPONSAVEL.COD_RESPONSAVEL = EMPRESA.COD_RESPONSAVEL
ORDER BY 1