SELECT COLABORADOR.ID_CONTRIBUINTE AS COD_EMP,
       COLABORADOR.TP_FUNCIONARIO AS TIPO,
       COLABORADOR.CODIGO_FUNCIONARIO AS COD_FUN,
       APONTAMENTO_INFO."DATA"
  FROM APONTAMENTO_INFO
 INNER JOIN APONTAMENTO
    ON APONTAMENTO.ID_APONTAMENTO = APONTAMENTO_INFO.ID_APONTAMENTO
 INNER JOIN COLABORADOR
    ON COLABORADOR.ID_COLABORADOR = APONTAMENTO.ID_COLABORADOR
 WHERE APONTAMENTO_INFO.IND_FALTA = 1
   AND APONTAMENTO_INFO.IND_EXCLUIDO IS NULL
 ORDER BY 1, 2, 3, 4;