SELECT COD_EMP,
       TIPO,
       COD_FUN,
       PAQUISI_INI,
       PAQUISI_FIM,
       DATA_PAGTO,
       DATA_AVISO_FERIAS,
       FERIAS_INI,
       FERIAS_FIM,
       ABONO_INI,
       ABONO_FIM
  FROM RECIBO
 WHERE PAQUISI_INI IS NOT NULL
   AND TIPO_RECIBO = 3
 ORDER BY COD_EMP,
          TIPO,
          COD_FUN,
          PAQUISI_INI;