SELECT func.COD_EMP,
		func.TIPO,
		func.COD_FUN,
		CAST(colc.DATA_ALTERACAO AS DATE) AS COMPETENCIA,
		colc.REMUNERACAO_VALOR AS VALOR_SALARIO,
		colc.DESCRICAO_ALTERACAO AS MOTIVO
FROM FUNCIONARIO func
INNER JOIN COLABORADOR cola
  ON cola.ID_CONTRIBUINTE = func.COD_EMP
  AND cola.CODIGO_FUNCIONARIO = func.COD_FUN
  AND cola.TP_FUNCIONARIO = func.TIPO
INNER JOIN COLABORADOR_CONTRATO colc
  ON colc.ID_COLABORADOR = cola.ID_COLABORADOR

UNION

SELECT func.COD_EMP,
		func.TIPO,
		func.COD_FUN,
		soc.COMPETENCIA AS COMPETENCIA,
		soc.PROLABORE AS VALOR_SALARIO,
		'' AS MOTIVO
FROM FUNCIONARIO func
INNER JOIN COLABORADOR cola
  ON cola.ID_CONTRIBUINTE = func.COD_EMP
  AND cola.CODIGO_FUNCIONARIO = func.COD_FUN
  AND cola.TP_FUNCIONARIO = func.TIPO
INNER JOIN SOCIO_INFO soc
  ON soc.ID_COLABORADOR = cola.ID_COLABORADOR
ORDER BY 1, 2, 3, 4