SELECT
DEPTO1.EMPRE_CUST,
DEPTO1.DEPTO_CUST,
DEPTO1.DESCR_CUST
FROM tblccust DEPTO1
WHERE DEPTO1.SECAO_CUST = (SELECT MAX(DEPTO2.SECAO_CUST)
                               FROM tblccust DEPTO2
							  WHERE DEPTO1.EMPRE_CUST = DEPTO2.EMPRE_CUST
							    AND DEPTO1.DEPTO_CUST = DEPTO2.DEPTO_CUST)
