SELECT
tblfunci.CODIG_EMPR,
tblfunci.CODIG_FUNC,
tblfunci.NOMFU_FUNC,
tblfunci.CONDI_FUNC,
tblfunci.ADMIS_FUNC,
tblfunci.NIVEL_FUNC,
tblfunci.SALAR_FUNC,
tblfunci.HORME_FUNC,
tblfunci.CSIND_FUNC,
tblfunci.CALEN_FUNC,
tblfunci.TIPOP_FUNC,
tblfunci.CDSIN_FUNC,
tblfunci.NASCT_FUNC,
tblfunci.EXPE1_FUNC,
tblfunci.EXPE2_FUNC,
(SELECT MAX(tblrescisao.DATRE_RESC) 
  FROM tblrescisao
  WHERE tblrescisao.CODIG_EMPR  = tblfunci.CODIG_EMPR
	AND tblrescisao.FUNCI_RESC = tblfunci.CODIG_FUNC) AS DTAFA_FUNC,
tblfunci.OPCAO_FUNC,
tblfunci.CTFGT_FUNC,
tblfunci.ADMFG_FUNC,
tblfunci.MOVIF_FUNC,
tblfunci.MUDED_FUNC,
tblfunci.ADMCG_FUNC,
tblfunci.ADMRA_FUNC,
tblfunci.CARGO_FUNC,
tblfunci.VINCU_FUNC,
tblfunci.CIVIL_FUNC,
tblfunci.SITUA_FUNC,
tblfunci.GRAUI_FUNC,
tblfunci.NACIO_FUNC,
tblfunci.SEXFU_FUNC,
tblfunci.NOCTP_FUNC,
tblfunci.SRCTP_FUNC,
tblfunci.UFCTP_FUNC,
tblfunci.NOCPF_FUNC,
tblfunci.NOPIS_FUNC,
tblfunci.RGFUN_FUNC,
tblfunci.ENDER_FUNC,
tblfunci.BAIRR_FUNC,
tblfunci.COMPL_FUNC,
tblfunci.MUNIC_FUNC,
tblfunci.ESTAD_FUNC,
tblfunci.CEPFU_FUNC,
tblfunci.DEPAR_FUNC,
tblfunci.SETOR_FUNC,
tblfunci.SECAO_FUNC,
tblfunci.OCORR_FUNC,
tblfunci.CATEG_FUNC,
tblfunci.MEDIC_FUNC,
tblfunci.FOTOF_FUNC,
tblfunci.RACAS_FUNC,
tblfunci.BANCO_FUNC,
tblfunci.AGENC_FUNC,
tblfunci.C_COR_FUNC,
tblfunci.TIPOC_FUNC,
tblfunci.DEFIC_FUNC,
tblfunci.MATRESOCIAL_FUNC,
(SELECT tblmunic.DESCR_MUNI
 FROM tblmunic
 WHERE tblmunic.CODIG_MUNI = tblcplfun.LNASC_FUNC) AS LOCAL_NASCTO,
tblcplfun.ESTAD_FUNC AS ESTADO,
tblcplfun.RESER_FUNC AS CART_RESERVISTA,
tblcplfun.CARES_FUNC AS CATE_RESERVISTA,
tblcplfun.TITEL_FUNC AS TITULO_ELEITOR,
tblcplfun.ZONAS_FUNC AS ZONA_ELEITOR,
tblcplfun.STIEL_FUNC AS SECAO_ELEITOR,
tblcplfun.DTPIS_FUNC AS DATA_PIS,
tblcplfun.ORGAO_FUNC AS IDENTIDADE_ORGAO,
tblcplfun.ESTRG_FUNC AS IDENTIDADE_ESTADO,
tblcplfun.EMIRG_FUNC AS IDENTIDADE_DATA,
tblcplfun.NODDD_FUNC AS DDD,
tblcplfun.NOTEL_FUNC AS FONE,
tblcplfun.NOCNH_FUNC AS CART_MOTO,
tblcplfun.VCCNH_FUNC AS CART_MOTO_VENC,
tblcplfun.HABPR_FUNC AS CART_MOTO_CATE,
tblcplfun.EMICT_FUNC AS CART_PROF_DATA
FROM tblfunci
LEFT JOIN tblcplfun
ON tblcplfun.CODIG_EMPR = tblfunci.CODIG_EMPR
AND tblcplfun.CODIG_FUNC = tblfunci.CODIG_FUNC;