SELECT
tblempr.*,
/* DADOS RESPONS√ÅVEL EMPRESA */
(SELECT SOCIOS.NUCPF_SOCI
   FROM tblsocio AS SOCIOS
  WHERE SOCIOS.RESPO_SOCI = 1
    AND SOCIOS.CODIG_EMPR = tblempr.CODIG_EMPR
	AND SOCIOS.CODIG_SOCI = (SELECT MAX(SOCIOS_AUX.CODIG_SOCI)
	                               FROM tblsocio AS SOCIOS_AUX
								  WHERE SOCIOS_AUX.RESPO_SOCI = 1
								    AND SOCIOS_AUX.CODIG_EMPR = SOCIOS.CODIG_EMPR)) AS RESP_CPF,
