SELECT LOTACAOFUNCIONARIO.ANOMES,
LOTACAOFUNCIONARIO.FUNCIONARIO,
LOTACAO.*,
ESTABELECIMENTO.CCC,
CIDADE.NOME AS NOME_CIDADE,
CIDADE.UF AS UF
FROM LOTACAOFUNCIONARIO
LEFT JOIN LOTACAO
ON LOTACAO.CODIGO = LOTACAOFUNCIONARIO.LOTACAO
AND LOTACAO.EMPRESA = LOTACAOFUNCIONARIO.EMPRESA
LEFT JOIN ESTABELECIMENTO 
ON LOTACAO.EMPRESA = ESTABELECIMENTO.EMPRESA
LEFT JOIN CIDADE
ON CIDADE.CODIGO = ESTABELECIMENTO.CIDADE
ORDER BY LOTACAOFUNCIONARIO.EMPRESA, LOTACAOFUNCIONARIO.FUNCIONARIO, LOTACAOFUNCIONARIO.ANOMES