SELECT ESTABELECIMENTO.*,
       EMPRESA.FILANTROPIA,
       EMPRESA.NATUREZAJURIDICA,
       CIDADE.NOME AS NOME_CIDADE,
       CIDADE.UF
  FROM EMPRESA
  LEFT JOIN ESTABELECIMENTO
    ON ESTABELECIMENTO.EMPRESA = EMPRESA.CODIGO
   AND REPLACE(REPLACE(REPLACE(REPLACE(ESTABELECIMENTO.CCC, '.', ''), '-', ''), '/', ''), ' ', '') = REPLACE(REPLACE(REPLACE(REPLACE(EMPRESA.CCC, '.', ''), '-', ''), '/', ''), ' ', '')
  LEFT JOIN CIDADE
    ON CIDADE.CODIGO = ESTABELECIMENTO.CIDADE
 WHERE ESTABELECIMENTO.EMPRESA IS NOT NULL
 ORDER BY 2;