SELECT (SELECT FUNCIONARIO.EMPRESA FROM FUNCIONARIO WHERE FUNCIONARIO.CODIGO = H.FUNCIONARIO ) AS EMPRESA 
, H.FUNCIONARIO 
-- Nas sub consultas foi necessario colocar o MAX devido a ter casos onde erroneamente o empregado possui dois horario para o mesmo dia
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 1 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS SEG
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 2 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS TER
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 3 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS QUA
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 4 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS QUI
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 5 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS SEX 
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 6 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS SAB 
, (SELECT MAX(HORARIOFUNCIONARIOITEM.HORARIOTRABALHO) FROM HORARIOFUNCIONARIOITEM WHERE HORARIOFUNCIONARIOITEM.DIAHORARIO = 7 AND HORARIOFUNCIONARIOITEM.IDHORARIOFUNCIONARIO = H.ID) AS DOM 
, (SELECT FUNCIONARIO.DIASSEMANA FROM FUNCIONARIO WHERE FUNCIONARIO.CODIGO = H.FUNCIONARIO) AS DIASSEMANA
, (SELECT FUNCIONARIO.DESCANSOSEMANAL FROM FUNCIONARIO WHERE FUNCIONARIO.CODIGO = H.FUNCIONARIO) AS DESCANSOSEMANAL
, (SELECT FUNCIONARIO.DIADESCANSOSEMANAL FROM FUNCIONARIO WHERE FUNCIONARIO.CODIGO = H.FUNCIONARIO) AS DIADESCANSOSEMANAL
, (SELECT FUNCIONARIO.HORASSABADOCOMPENSADA FROM FUNCIONARIO WHERE FUNCIONARIO.CODIGO = H.FUNCIONARIO) AS HORASSABADOCOMPENSADA
FROM HORARIOFUNCIONARIO H
ORDER BY EMPRESA, FUNCIONARIO
