SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE "WP-PESSOAL_AFASTAMENTOS".EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,       
       (SELECT "WP-PESSOAL".CODIGO
		  FROM "WP-PESSOAL", 
		       "WP-PESSOAL_CONTRATOS"
		 WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "WP-PESSOAL".EMPRESA_ID
		   AND "WP-PESSOAL_CONTRATOS".PESSOAL_ID = "WP-PESSOAL".PESSOAL_ID		
		   AND "WP-PESSOAL_AFASTAMENTOS".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID ) AS EMPREGADO,
	   "WP-PESSOAL_AFASTAMENTOS".DATA_SAIDA,
	   "WP-PESSOAL_AFASTAMENTOS".COD_SEFIP_SAIDA,
	   "WP-PESSOAL_AFASTAMENTOS".DATA_RETORNO
FROM "WP-PESSOAL_AFASTAMENTOS"
ORDER BY 1,2,3