SELECT "G-BANCOS_AGENCIAS".BANCOS_ID,
"G-BANCOS_AGENCIAS".BANCOS_AGENCIAS_ID,
"G-BANCOS".CODIGO,
"G-BANCOS".NUMERO,
"G-BANCOS_AGENCIAS".NRO_AGENCIA,
"G-BANCOS_AGENCIAS".DIGITO_AGENCIA,
"G-BANCOS".NOME,
"G-BANCOS_AGENCIAS".NOME_AGENCIA,
"G-BANCOS_AGENCIAS".BAIRRO,
"G-BANCOS".LAYOUT_CHEQUE
FROM "G-BANCOS_AGENCIAS", "G-BANCOS"
WHERE "G-BANCOS_AGENCIAS".BANCOS_ID = "G-BANCOS".BANCOS_ID
ORDER BY "G-BANCOS_AGENCIAS".BANCOS_ID, "G-BANCOS_AGENCIAS".BANCOS_AGENCIAS_ID