SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE "WP-DEPENDENTES".EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
(SELECT "WP-PESSOAL".CODIGO FROM "WP-PESSOAL" WHERE "WP-DEPENDENTES".EMPRESA_ID = "WP-PESSOAL".EMPRESA_ID AND "WP-DEPENDENTES".PESSOAL_ID = "WP-PESSOAL".PESSOAL_ID) AS EMPREGADO,
"WP-DEPENDENTES".DEPENDENTES_ID,
"WP-DEPENDENTES".NOME,
"WP-DEPENDENTES".NASCIM_SEXO,
"WP-DEPENDENTES".NASCIM_DATA,
"WP-DEPENDENTES".CPF,
"WP-DEPENDENTES".GRAU_PARENTESCO,
"WP-DEPENDENTES".NASCIM_MATRICULA,
"WP-DEPENDENTES".NASCIM_CARTORIO,
"WP-DEPENDENTES".NASCIM_REGISTRO,
"WP-DEPENDENTES".NASCIM_LIVRO,
"WP-DEPENDENTES".NASCIM_FOLHA,
"WP-DEPENDENTES".NASCIM_NASCIDO_VIVO,
"WP-DEPENDENTES".DEP_SAL_FAMILIA,
"WP-DEPENDENTES".DEP_IRF,
"WP-DEPENDENTES".DEP_IRF_IDADE
FROM "WP-DEPENDENTES"
WHERE "WP-DEPENDENTES".DEP_SAL_FAMILIA = 'T'
OR "WP-DEPENDENTES".DEP_IRF = 'S'
ORDER BY EMPRESA, EMPREGADO, "WP-DEPENDENTES".DEPENDENTES_ID