SELECT "G_CADASTRO_BASICO".CADASTRO_BASICO_ID,
       "G_CADASTRO_BASICO".DOCUMENTO_NUMERO,
       "G_CADASTRO_BASICO".NOME,
       "G_CADASTRO_BASICO".TELEFONE,
       "G_ENDERECOS".CEP,
       "G_ENDERECOS".LOGRADOURO,
       "G_ENDERECOS".NUMERO,
       "G_ENDERECOS".COMPLEMENTO,
       "G_ENDERECOS".BAIRRO,
       "G_ENDERECOS".UF,
       "G_ENDERECOS".MUNICIPIOS_ID,
       (SELECT "G-MUNICIPIOS".CODIGO_IBGE FROM "G-MUNICIPIOS" WHERE "G_ENDERECOS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS CODIGO_IBGE
 FROM "G_CADASTRO_BASICO" LEFT OUTER JOIN "G_ENDERECOS"
   ON ("G_ENDERECOS".ENDERECOS_ID = "G_CADASTRO_BASICO".ENDERECO_ID) 
 WHERE EXISTS ( SELECT 1 
                  FROM "WP-PESSOAL_CONTRATOS"
                 WHERE "WP-PESSOAL_CONTRATOS".ESTAGIO_AGENTEINTEGRACAO_ID = "G_CADASTRO_BASICO".CADASTRO_BASICO_ID)
 ORDER BY 1
