SELECT "G-CONTABILISTAS".CODIGO,
       "G-CONTABILISTAS".CODIGO_EXTERNO,
       "G-CONTABILISTAS".NOME,
       "G-CONTABILISTAS".CPF
  FROM "G-CONTABILISTAS" 
 WHERE EXISTS ( SELECT 1 
                  FROM "WP-PESSOAL_CONTRATOS"
                 WHERE "WP-PESSOAL_CONTRATOS".ESTAGIO_RESPONSAVEL_ID = "G-CONTABILISTAS".CODIGO)
 ORDER BY 1
