SELECT DISTINCT(SELECT "G-EMPRESAS".CODIGO_EXTERNO 
          FROM "G-EMPRESAS",
               "WP-PESSOAL_CONTRATOS"
         WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "G-EMPRESAS".CODIGO
           AND "WP-PERIODOS_AQUISITIVOS".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID) AS EMPRESA,
       (SELECT "WP-PESSOAL".CODIGO
		  FROM "WP-PESSOAL", 
		       "WP-PESSOAL_CONTRATOS"
		 WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "WP-PESSOAL".EMPRESA_ID
		   AND "WP-PESSOAL_CONTRATOS".PESSOAL_ID = "WP-PESSOAL".PESSOAL_ID		
		   AND "WP-PERIODOS_AQUISITIVOS".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID ) AS EMPREGADO,
		"WP-PERIODOS_AQUISITIVOS".DATA_INICIO AS INICIO_AQUISITIVO,
       	"WP-PERIODOS_AQUISITIVOS".DATA_FIM AS FIM_AQUISITIVO,
		"WP-PERIODOS_AQUISITIVOS".PERIODOS_AQUISITIVOS_ID,
        "WP-GOZO_FERIAS".DATA_INICIO AS INICIO_GOZO,
		"WP-GOZO_FERIAS".DATA_FIM AS FIM_GOZO,
		"WP-GOZO_FERIAS".DIAS_GOZO,
		"WP-GOZO_FERIAS".DIAS_VENDIDOS,
		"WP-GOZO_FERIAS".MAIOR_REMUN,
		"WP-GOZO_FERIAS".DEDUZIR_FERIADOS_SN,
		"WP-GOZO_FERIAS".DIAS_FERIADOS,
		"WP-GOZO_FERIAS".COMPETENCIA,
		"WP-GOZO_FERIAS".NUMERO_FOLHA,
		"WP-GOZO_FERIAS".FERIAS_RATEIO_SN,
		"WP-GOZO_FERIAS".GOZO_ID_ORIGEM,
		"WP-GOZO_FERIAS".DIAS_FALTAS_INFORMADAS,
		"WP-GOZO_FERIAS".DIAS_FALTAS_PROCESSADAS,
		"WP-GOZO_FERIAS".DIAS_DIREITO,
		"WP-GOZO_FERIAS".DIAS_REDUCAO,
		"WP-GOZO_FERIAS".ANTECIPAR_DIAS_DIREITO,
		"WP-GOZO_FERIAS".DESCONSIDERAR_DIAS_REDUCAO,
		"WP-GOZO_FERIAS".DIAS_AFASTADOS
FROM "WP-GOZO_FERIAS" 
 left outer join "WP-PERIODOS_AQUISITIVOS"
 on ( "WP-GOZO_FERIAS".PERIODOS_AQUISITIVOS_ID = "WP-PERIODOS_AQUISITIVOS".PERIODOS_AQUISITIVOS_ID)
 WHERE "WP-GOZO_FERIAS".DATA_INICIO IS NOT NULL
ORDER BY 1,2,3