SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE "G-EMPRESAS_FILIAIS".EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
"G-FILIAIS".CODIGO_EXTERNO,
"G-FILIAIS".DESCRICAO,
"G-FILIAIS".TIPO_LOGRADOURO,
"G-FILIAIS".ENDERECO,
"G-FILIAIS".ENDERECO_NUMERO,
"G-FILIAIS".ENDERECO_COMPLEMENTO,
"G-FILIAIS".BAIRRO,
(SELECT "G-MUNICIPIOS".DESCRICAO FROM "G-MUNICIPIOS" WHERE "G-FILIAIS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS CIDADE,
(SELECT "G-MUNICIPIOS".UF FROM "G-MUNICIPIOS" WHERE "G-FILIAIS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS ESTADO,
(SELECT "G-MUNICIPIOS".CODIGO_IBGE FROM "G-MUNICIPIOS" WHERE "G-FILIAIS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS CODIGO_IBGE,
"G-FILIAIS".TELEFONE_DDD,
"G-FILIAIS".TELEFONE,
"G-FILIAIS".CNPJ,
"G-FILIAIS".CEP,
"G-FILIAIS".CPF,
"G-FILIAIS".CEI,
"G-FILIAIS".INSCRICAOESTADUAL,
"G-FILIAIS".FILIAL_CENTRALIZADORA,
"G-EMPRESAS_FILIAIS".CNAEFISCAL,
"G-EMPRESAS_FILIAIS".GPS_COD_PGTO,
"G-EMPRESAS_FILIAIS".SINDICATOS_ID,
(SELECT "WP-FPAS".COD_FPAS FROM "WP-FPAS" WHERE "G-EMPRESAS_FILIAIS".FPAS_ID = "WP-FPAS".FPAS_ID) AS COD_FPAS,
(SELECT "WP-FPAS".INSS_PERC_EMPRESA FROM "WP-FPAS" WHERE "G-EMPRESAS_FILIAIS".FPAS_ID = "WP-FPAS".FPAS_ID) AS INSS_PERC_EMPRESA,
(SELECT "WP-FPAS".TERCEIROS_CODIGO FROM "WP-FPAS" WHERE "G-EMPRESAS_FILIAIS".FPAS_ID = "WP-FPAS".FPAS_ID) AS TERCEIROS_CODIGO,
(SELECT "WP-FPAS".TERCEIROS_PERC FROM "WP-FPAS" WHERE "G-EMPRESAS_FILIAIS".FPAS_ID = "WP-FPAS".FPAS_ID) AS TERCEIROS_PERC,
(SELECT "WP-FPAS".INSS_PERC_EMPREGADORES_AUT FROM "WP-FPAS" WHERE "G-EMPRESAS_FILIAIS".FPAS_ID = "WP-FPAS".FPAS_ID) AS INSS_PERC_EMPREGADORES_AUT,
"G-FILIAIS".TIPO_CAEPF
FROM "G-EMPRESAS_FILIAIS", "G-FILIAIS"
WHERE "G-EMPRESAS_FILIAIS".EMPRESA_ID = "G-FILIAIS".EMPRESA_ID
AND "G-EMPRESAS_FILIAIS".FILIAIS_ID = "G-FILIAIS".CODIGO
ORDER BY "G-EMPRESAS_FILIAIS".EMPRESA_ID