SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE WP_HORARIOS.EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
       WP_HORARIOS.CODIGO,
	   WP_HORARIOS.DESCRICAO,
	   WP_HORARIOS.HORA_ENTRADA,
	   WP_INTERVALOS.HORA_INICIO AS INICIO_INTERVALO,
	   WP_INTERVALOS.HORA_TERMINO AS TERMINO_INTERVALO,
	   WP_HORARIOS.HORA_SAIDA,
	   WP_HORARIOS.VALIDADE_INICIO,
	   WP_HORARIOS.VALIDADE_TERMINO
FROM WP_HORARIOS
LEFT JOIN WP_INTERVALOS
ON (WP_HORARIOS.HORARIOS_ID = WP_INTERVALOS.HORARIOS_ID)
ORDER BY 1, 2