SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE WP_JORNADAS.EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
       WP_JORNADAS.CODIGO,
	   WP_JORNADAS.DESCRICAO,
	   WP_JORNADAS.TIPO_JORNADA,
	   WP_JORNADAS.HORARIO_FLEXIVEL,
	   WP_JORNADAS.CARGAHORARIASEMANAL,
	   WP_JORNADAS.CARGAHORARIAMENSAL	   
FROM WP_JORNADAS
ORDER BY 1, 2