SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE WP_JORNADAS.EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
       WP_JORNADAS.CODIGO AS JORNADA,
       WP_HORARIOS.CODIGO  AS HORARIO,
       WP_DIAS.TIPO_DIA,
       WP_DIAS.MODO_DIA	   
FROM WP_DIAS
LEFT JOIN WP_JORNADAS
ON (WP_DIAS.JORNADAS_ID = WP_JORNADAS.JORNADAS_ID)
LEFT JOIN WP_HORARIOS
ON (WP_DIAS.HORARIOS_ID = WP_HORARIOS.HORARIOS_ID)
ORDER BY 1,2,4,2