SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO 
          FROM "G-EMPRESAS",
               "WP-PESSOAL_CONTRATOS"
         WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "G-EMPRESAS".CODIGO
           AND "WP-PERIODOS_AQUISITIVOS".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID) AS EMPRESA,
       (SELECT "WP-PESSOAL".CODIGO
		  FROM "WP-PESSOAL", 
		       "WP-PESSOAL_CONTRATOS"
		 WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "WP-PESSOAL".EMPRESA_ID
		   AND "WP-PESSOAL_CONTRATOS".PESSOAL_ID = "WP-PESSOAL".PESSOAL_ID		
		   AND "WP-PERIODOS_AQUISITIVOS".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID ) AS EMPREGADO,
       	"WP-PERIODOS_AQUISITIVOS".DATA_INICIO,
       	"WP-PERIODOS_AQUISITIVOS".DATA_FIM,
       	COALESCE(( SELECT  MAX("WP-GOZO_FERIAS".DIAS_DIREITO) 
		    FROM "WP-GOZO_FERIAS" 
		   WHERE "WP-GOZO_FERIAS".PERIODOS_AQUISITIVOS_ID = "WP-PERIODOS_AQUISITIVOS".PERIODOS_AQUISITIVOS_ID 
		     AND "WP-GOZO_FERIAS".DIAS_DIREITO IS NOT NULL), 30) AS DIAS_DIREITO,
		COALESCE(( SELECT  MAX("WP-GOZO_FERIAS".DIAS_VENDIDOS) 
		    FROM "WP-GOZO_FERIAS" 
		   WHERE "WP-GOZO_FERIAS".PERIODOS_AQUISITIVOS_ID = "WP-PERIODOS_AQUISITIVOS".PERIODOS_AQUISITIVOS_ID 
		     AND "WP-GOZO_FERIAS".DIAS_DIREITO IS NOT NULL), 0) AS DIAS_ABONO,
		"WP-PERIODOS_AQUISITIVOS".PERIODOS_AQUISITIVOS_ID
FROM "WP-PERIODOS_AQUISITIVOS"
WHERE "WP-PERIODOS_AQUISITIVOS".DATA_INICIO IS NOT NULL
ORDER BY 1,2,3