SELECT (SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE "WP-EVENTOS_EMPRESAS".EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
       "WP-EVENTOS".CODIGO,
       "WP-EVENTOS".DESCRICAO,
       "WP-EVENTOS".TIPO,
       "WP-EVENTOS".ENTRADA_TIPO,
       "WP-EVENTOS".SIGLA_SAIDA,
       "WP-EVENTOS".FORMULA,
       CASE 
       	WHEN UPPER("WP-EVENTOS".DESCRICAO) LIKE '%MEDIA%' AND UPPER("WP-EVENTOS".DESCRICAO) LIKE '%13%' THEN 'D'
       	WHEN UPPER("WP-EVENTOS".DESCRICAO) LIKE '%MEDIA%' AND UPPER("WP-EVENTOS".DESCRICAO) LIKE '%FÃ‰RIAS%' THEN 'F'
       	ELSE ''
       END AS FAZ_MEDIA
  FROM "WP-EVENTOS_EMPRESAS" LEFT OUTER JOIN "WP-EVENTOS"       
 ON ("WP-EVENTOS_EMPRESAS".EVENTOS_ID = "WP-EVENTOS".EVENTOS_ID)
 WHERE "WP-EVENTOS".DATA_INATIVACAO IS NULL
 ORDER BY 1,2